import styled from 'styled-components';

// export const DirectoryMenuContainer = styled.div`
//   width: 100%;
//   display: flex;
//   flex-wrap: wrap;
//   justify-content: space-between;
// `;

export const DirectoryMenuContainer = styled.div`
  width: 100%;
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-template-rows: repeat(2, 30rem);
  gap: 2.5rem;

  /* & div:nth-child(1) {
    grid-area: 1 / 1 / 3 / 2;
  }

  & div:nth-child(2) {
    grid-area: 1 / 2 / 2 / 4;
  }

  & div:nth-child(3) {
    grid-area: 2 / 2 / 3 / 4;
  } */

  & div:first-child {
    grid-row: 1 / span 2;
  }

  & div:not(:first-child) {
    grid-column: 2 / span 2;
  }
`;
