import styled from 'styled-components';

export const FooterContainer = styled.footer`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  height: 25rem;
  background-color: #28150e;
  color: #fcf9f8;
  margin-top: 2rem;

  & div {
    display: flex;
    flex-direction: column;
  }
`;
