import { createSelector } from 'reselect';

const selectShop = state => state.shop;

export const selectCollections = createSelector(
  [selectShop],
  shop => shop.collections
);

export const selectCollectionsForPreview = createSelector(
  [selectCollections],
  collections =>
    collections ? Object.keys(collections).map(key => collections[key]) : []
);

export const selectCollection = ownProps => {
  console.log('selector:', ownProps);
  let collectionUrlParam = '';
  if (ownProps.match.params.collectionId) {
    collectionUrlParam = ownProps.match.params.collectionId;
  } else {
    collectionUrlParam = 'mens';
  }
  return createSelector([selectCollections], collections =>
    collections ? collections[collectionUrlParam] : null
  );
};

export const selectIsCollectionFetching = createSelector(
  [selectShop],
  shop => shop.isFetching
);

export const selectIsCollectionLoaded = createSelector(
  [selectShop],
  shop => !!shop.collections
);
