import styled from 'styled-components';

export const HeroContainer = styled.div`
  position: relative;

  h3 {
    position: absolute;
    top: 18rem;
    left: 5rem;
    color: white;
    font-size: 4.2rem;
    font-weight: 300;
  }
`;
