import React from 'react';
import { connect } from 'react-redux';

import CollectionItem from '../../components/collection-item/collection-item.component';

import { selectCollection } from '../../redux/shop/shop.selectors';

import {
  CollectionPageContainer,
  CollectionTitle,
  CollectionItemsContainer,
} from './collection.styles';

const CollectionPage = ({ collection }) => {
  const { title, items } = collection;
  return (
    <CollectionPageContainer>
      <CollectionTitle>{title}</CollectionTitle>
      <CollectionItemsContainer>
        {items.map(item => (
          <CollectionItem key={item.id} item={item} />
        ))}
      </CollectionItemsContainer>
    </CollectionPageContainer>
  );
};

const mapStateToProps = (state, ownProps) => {
  // if (!ownProps.match.params.collectionId) console.log('not there');
  console.log('op:', ownProps);
  // const { collectionId = 'mens' } = ownProps.match.params;
  // ownProps = ownProps.match ? 'mens' : ownProps.match.params.collectionId;
  // collectionId = collectionId ? collectionId : 'mens';

  console.log('id:', ownProps.match.params.collectionId);
  console.log('type:', typeof ownProps.match.params.collectionId);
  return {
    collection: selectCollection(ownProps.match.params.collectionId)(state),
  };
};

export default connect(mapStateToProps)(CollectionPage);
