import styled from 'styled-components';

export const HeroContainer = styled.div`
  position: relative;

  div {
    position: absolute;
    top: 18rem;
    left: 5rem;
    color: white;
    font-size: 5rem;
    font-weight: 300;

    p {
      font-size: 3rem;
    }
  }
`;
