import styled from 'styled-components';

// @import '../../variables';

export const SideDrawerContainer = styled.div`
  position: absolute;
  top: 0;
  right: 0;
  height: 100vh;
  width: 0;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: var(--color-primary);
  padding-top: 6rem;
  z-index: 100;
  transition: 300ms ease all;

  &.open {
    width: 70vw;
    box-shadow: 0px 0px 10px 10px hsla(24, 50%, 23%, 0.3);
  }

  nav {
    @media only screen and (max-width: 500px) {
      display: block;
    }

    ul {
      display: flex;
      flex-direction: column;
      align-items: center;

      li {
        margin: 2rem 0;
      }
    }
  }
`;

export const CloseBtn = styled.button`
  position: absolute;
  top: 0px;
  right: 0px;
  font-size: 3rem;
  line-height: 3rem;
  background-color: unset;
  border: unset;
  outline: none;
  color: white;
  padding: 2rem;
  cursor: pointer;

  &:hover {
    // color: var(--color-secondary);
  }
`;
