import styled from 'styled-components';

export const HeroContainer = styled.div`
  position: relative;
  overflow: hidden;

  img {
    width: 100%;
    height: auto;
  }

  div {
    position: absolute;
    top: 18rem;
    left: 5rem;
    color: white;
    font-size: 5rem;
    font-weight: 300;

    @media screen and (max-width: 800px) {
      top: 12rem;
      font-size: 4rem;
    }

    p {
      font-size: 2.5rem;
      margin-bottom: 2.2rem;
    }

    button {
      font-family: inherit;
    }
  }
`;
