import React from 'react';

import CustomButton from '../../components/custom-button/custom-button.component';

import { HeroContainer } from './hero.styles';

const Hero = ({ title, subTitle, button, bgImage }) => (
  <HeroContainer>
    <div>
      <h3>{title}</h3>
      <p>{subTitle}</p>
      {button ? <CustomButton inverted>{button}</CustomButton> : null}
    </div>
    <img src={bgImage} alt='' />
  </HeroContainer>
);

export default Hero;
